import React, { useRef, useState } from 'react'
import PostForm from './components/PostForm'
//import Counter from './components/Counter'
import Postitem from './components/PostItem'
import PostList from './components/PostList'
import MyButton from './components/UI/button/MyButton'
import MyInput from './components/UI/input/MyInput'
import MySelect from './components/UI/select/MySelect'
import './styles/App.css'

function App() {
  const [posts, setPosts] = useState([
    { id: 1, title: 'Javascript', body: 'ggg' },
    { id: 2, title: 'Php', body: 'hhh' },
    { id: 3, title: 'C++', body: 'ooo' },
  ])

  const [selectedSort, setSelectedSort] = useState('')
  const [searchQuery, setSearchQuery] = useState('')

  function getSortedPosts() {
    console.log('f')
    if (selectedSort) {
      return [...posts].sort((a, b) => a[selectedSort].localeCompare(b[selectedSort]))
    } return posts;
  }

  const sortedPosts = getSortedPosts()

  const createPost = (newPost) => {
    setPosts([...posts, newPost])
  }

  //отримуємо пост з дочернього елементу
  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))
  }

  const sortPosts = (sort) => {
    setSelectedSort(sort);
  }

  return (
    <div className="App">
      <PostForm create={createPost} />
      <hr style={{ margin: "15px 0" }} />
      <div>
        <MyInput
          value={searchQuery}
          onChange={e => setSearchQuery(e.target.value)}
          placeholder="Пошук..."
        />
        <MySelect
          className={selectedSort}
          value={selectedSort}
          onChange={sortPosts}
          defaultValue="Сортування по"
          options={[
            { value: 'title', name: 'За назвою' },
            { value: 'body', name: 'По опису' },
          ]}
        />
      </div>
      {posts.length
        ? < PostList remove={removePost} posts={sortedPosts} title="Список постів" />
        : <h1 style={{ textAlign: 'center', marginTop: '50px' }}>Пости не знайдені!</h1>
      }
    </div >
  );
}

export default App;
